CREATE TABLE IF NOT EXISTS ai_actions_log(id INTEGER PRIMARY KEY AUTOINCREMENT,tenant_id TEXT,action_type TEXT,payload_before TEXT,payload_after TEXT,context TEXT,status TEXT,created_at TEXT DEFAULT (datetime('now')),reverted_at TEXT);CREATE TABLE IF NOT EXISTS ai_tasks(id INTEGER PRIMARY KEY AUTOINCREMENT,tenant_id TEXT,title TEXT NOT NULL,due_at TEXT,status TEXT DEFAULT 'open',created_at TEXT DEFAULT (datetime('now')));